mapper.select:
SELECT

FROM sys_likes a

WHERE a.id = #{id}

SELECT

FROM sys_likes a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

SELECT

FROM sys_likes a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE sys_likes SET
	foreign_id = #{foreignId},
	user_id = #{userId},
	ip = #{ip},
	token = #{token}
WHERE id = #{id}

UPDATE sys_likes SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
select count(1) from sys_likes where foreign_id = #{foreignId}

	and user_id = #{userId}



		and ip = #{ip}



			and token = #{token}

select foreign_id,count(1) cc from sys_likes where foreign_id in

	#{foreignId}


	and user_id = #{userId}



		and ip = #{ip}



			and token = #{token}



group by foreign_id
