mapper.select:
SELECT

FROM project_declare a

WHERE a.id = #{id}

SELECT

FROM project_declare a

WHERE a.number = #{number}

SELECT a.id AS id, a.year AS year, a.proc_ins_id AS "procInsId", a.number AS number, a.name AS name, a.type AS type, a.status AS status, a.leader AS leader, AS snames, AS tnames, AS typeString, AS levelString, AS leaderString, AS finalResultString FROM project_declare a LEFT JOIN sys_user sysu ON a.leader=sysu.id LEFT JOIN sys_office syso ON syso.id=sysu.office_id
a.del_flag = #{DEL_FLAG_NORMAL} AND a.status!='0' ${sqlMap.officeMap} ${sqlMap.levelMap}
AND a.type = #{type}
AND a.level = #{level}
AND a.year = #{year}
AND a.status = #{status}
AND a.number like concat('%',#{number},'%')
AND a.name like concat('%',#{name},'%')
AND like concat('%',#{leader},'%')
AND
like concat('%',#{leaderString},'%')
AND a.apply_time >= #{startDate}
AND a.apply_time < #{endDate}
AND ( a.number like concat('%',#{keyword},'%') or a.name like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') )
ORDER BY CONVERT(${page.orderBy} using GBK) ${page.orderByType}
ORDER BY a.update_date DESC

SELECT a.id AS id, a.proc_ins_id AS "procInsId", a.number AS number, a.name AS name, a.type AS type, a.year AS year, a.level AS level, a.mid_score AS midScore, a.mid_result AS midResult, a.final_score AS finalScore, a.reply_score AS replyScore, a.status AS status,
AS typeString, a.leader AS leader,
AS leaderString, sn.name AS snames, sr.number AS snumber, tn.names AS tnames,
AS levelString,
AS finalResultString FROM project_declare a LEFT JOIN sys_user sysu ON a.leader=sysu.id LEFT JOIN sys_office syso ON syso.id=sysu.office_id LEFT JOIN ( SELECT t.pro_id, GROUP_CONCAT(u. NAME SEPARATOR '/') AS name FROM team_user_history t LEFT JOIN sys_user u ON t.user_id = u.id AND t.user_type = '1' GROUP BY t.pro_id
) sn ON sn.pro_id = a.id LEFT JOIN ( SELECT t.pro_id, count(*) AS number FROM team_user_history t LEFT JOIN sys_user u ON t.user_id = u.id AND t.user_type = '1' GROUP BY t.pro_id ) sr ON sr.pro_id = a.id LEFT JOIN ( SELECT t.pro_id, GROUP_CONCAT(u. NAME SEPARATOR '/') AS names FROM team_user_history t LEFT JOIN sys_user u ON t.user_id = u.id AND t.user_type = '2' GROUP BY t.pro_id ) tn ON tn.pro_id = a.id
a.del_flag = #{DEL_FLAG_NORMAL} ${sqlMap.officeMap} ${sqlMap.levelMap} AND a.status!='0'
AND a.id in
#{id}
AND 1=2
AND a.type = #{type}
AND a.level = #{level}
AND a.year = #{year}
AND a.status = #{status}
AND a.number like concat('%',#{number},'%')
AND a.name like concat('%',#{name},'%')
AND like concat('%',#{leader},'%')
AND
like concat('%',#{leaderString},'%')
AND a.apply_time >= #{startDate}
AND a.apply_time < #{endDate}
AND ( a.number like concat('%',#{keyword},'%') or a.name like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') )
ORDER BY CONVERT(${page.orderBy} using GBK) ${page.orderByType}
ORDER BY find_in_set(a.id,REPLACE( ${id},' ','') )

SELECT

FROM project_declare a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

