mapper.select:
SELECT p.id, GROUP_CONCAT(u.name SEPARATOR '/') pname, t.user_type team_user_type FROM project_declare p LEFT JOIN team_user_history t on p.team_id=t.team_id AND p.id= t.pro_id LEFT JOIN sys_user u ON t.user_id = u.id WHERE
p.id=#{proid}
group by p.id,t.user_type

SELECT p.id, GROUP_CONCAT(u.name SEPARATOR '/') pname, t.user_type team_user_type FROM pro_model p LEFT JOIN team_user_history t on p.team_id=t.team_id AND p.id= t.pro_id LEFT JOIN sys_user u ON t.user_id = u.id WHERE
p.id=#{proid}
group by p.id,t.user_type

SELECT t.id,t.status,t.name,t.number FROM
project_declare t WHERE t.del_flag='0' AND t.status!='8' AND
t.status!='9' AND t.leader=#{uid}

SELECT t.id,t.status,t.name,t.number FROM
project_declare t WHERE t.del_flag='0' AND t.status!='8' AND
t.status!='9' AND t.team_id=#{tid}

SELECT count(1)
FROM project_declare t
WHERE t.del_flag='0' AND
t.leader=#{cby} AND t.name=#{name}

	AND t.id!=#{id}

SELECT p.*
FROM project_declare p
WHERE p.del_flag='0'

	AND p.number=#{num}


	and p.name=#{name}


	AND p.leader=#{uid}

SELECT distinct t.name,t.id FROM project_declare t LEFT JOIN team_user_relation f on t.team_id = f.team_id AND f.del_flag='0'
WHERE t.actyw_id=#{actywId} AND t.del_flag='0' AND f.user_id=#{uid}
order by t.create_date desc

