mapper.select:
SELECT a.id AS proModelId, a.number AS number, a.`name` AS p_name, a.year AS year, '' AS stage, '1' AS pro_type, a.type AS type, AS proCategoryName, AS levelName, AS finalStatus,
a.level AS "level", a.`name` AS short_name, a.introduction AS introduction, a.final_result AS finalStatusType, a.team_id AS teamId, t.name AS teamName, a.source AS source, AS sourceName, a.result_type AS resultType, GROUP_CONCAT(sdrt.label SEPARATOR ',') AS resultTypeName, a.result_content AS resultContent, a.budget AS budget, a.budget AS budgetDollar, a.innovation AS innovation, a.plan_step AS planStep, a.plan_content AS planContent, a.plan_start_date AS planStartDate, a.plan_end_date AS planEndDate, o6.name AS "office_name",
lstu.no AS "no", lstu.name "leader.name", lstu.no "leader.no", lop.name "leader.professional", lss.enterDate "leader.enterDate", lss.graduation "leader.graduation", sd.label "leader.education", lstu.id_no "leader.id_no", lstu.mobile "leader.mobile", lstu.email "leader.email", '' AS "leader.teacherTecTitle" FROM project_declare a LEFT JOIN act_yw yw ON yw.id = a.actyw_id LEFT JOIN sys_user u ON u.id = a.leader LEFT JOIN sys_office o6 ON o6.id = u.office_id LEFT JOIN team t ON t.id = a.team_id /** 团队负责人 **/ LEFT JOIN sys_user lstu ON lstu.id = a.leader LEFT JOIN sys_student_expansion lss ON lstu.id = lss.user_id LEFT JOIN sys_office lop ON lop.id = lstu.professional LEFT JOIN sys_dict sd ON lstu.education = sd.value AND sd.type = 'enducation_level' LEFT JOIN sys_dict sdrt ON FIND_IN_SET(sdrt.value, a.result_type) AND sdrt.type = 'project_result_type'
actyw_id = #{actywId} AND a.del_flag = '0' AND a.status!='0'
AND a.id in
#{id}
AND a.type = #{type}
AND a.level = #{level}
AND a.year = #{year}
AND a.status = #{status}
AND a.number like concat('%',#{number},'%')
AND a.name like concat('%',#{name},'%')
AND like concat('%',#{leader},'%')
AND
like concat('%',#{leaderString},'%')
AND a.apply_time >= #{startDate}
AND a.apply_time < #{endDate}
AND ( a.number like concat('%',#{keyword},'%') or a.name like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') or
like concat('%',#{keyword},'%') )
GROUP BY a.id ORDER BY CONVERT(${page.orderBy} using GBK) ${page.orderByType}
GROUP BY a.id ORDER BY a.update_date DESC
