mapper.select:
select a.id promodelid, a.p_name name, s1.label type, s2.label groupName, u.name leaderName, date_format(se.enterDate,'%Y') leaderEnter, p.name leaderPro, (case when u.id_type='1' then u.id_no else '' end) leaderIdNo, u.no leaderNo,u.mobile leaderMobile,1 teamNum,o.name office,a.introduction summary from pro_model a left join sys_dict s1 on s1.type='competition_net_type' and s1.value=a.pro_category left join sys_dict s2 on s2.type='gcontest_level' and s2.value=a.level left join sys_user u on a.declare_id=u.id left join sys_student_expansion se on se.user_id=u.id left join sys_office p on u.professional=p.id left join sys_office o on u.office_id=o.id where a.id in
#{id}
AND a.year = #{year}
AND o.id = #{deuser.office.id}
AND ( a.p_name LIKE concat('%',#{queryStr},'%') OR a.competition_number LIKE concat('%',#{queryStr},'%') OR u.name LIKE concat('%',#{queryStr},'%') )
order by a.sub_time

select t.id promodelid, u.name, o.name office, p.name profess,date_format(se.enterDate,'%Y') enter, (case when se.curr_state='1' then s1.label else s2.label end) xueli, (case when u.id_type='1' then u.id_no else '' end) idNo, u.no,u.mobile from pro_model t left join team_user_history team on team.pro_id=t.id and team.user_type='1' and team.user_id!=t.declare_id and team.del_flag='0' left join sys_user u on team.user_id=u.id left join sys_student_expansion se on se.user_id=u.id left join sys_dict s1 on s1.type='degree_type' and s1.value=se.instudy left join sys_dict s2 on s2.type='enducation_level' and s2.value=u.education left join sys_office p on u.professional=p.id left join sys_office o on u.office_id=o.id left join sys_user ul on t.declare_id=ul.id where t.id in
#{id}
AND t.year = #{year}
AND ul.office_id = #{deuser.office.id}
AND ( t.p_name LIKE concat('%',#{queryStr},'%') OR t.competition_number LIKE concat('%',#{queryStr},'%') OR ul.name LIKE concat('%',#{queryStr},'%') )
order by u.create_date

select a.id promodelid,u.name, u.no,te.technical_title zhicheng from pro_model a left join team_user_history team on team.pro_id=a.id and team.user_type='2' and team.del_flag='0' left join sys_user u on team.user_id=u.id left join sys_teacher_expansion te on te.user_id=u.id left join sys_user ul on a.declare_id=ul.id where a.id in
#{id}
AND a.year = #{year}
AND ul.office_id = #{deuser.office.id}
AND ( a.p_name LIKE concat('%',#{queryStr},'%') OR a.competition_number LIKE concat('%',#{queryStr},'%') OR ul.name LIKE concat('%',#{queryStr},'%') )
order by u.create_date

