mapper.select:
SELECT

FROM pro_model_tlxy a

WHERE a.id = #{id}

SELECT

FROM pro_model_tlxy a

WHERE pm.declare_id = #{declareId}
and pm.actyw_id = #{actywId}
and a.del_flag = '0'

SELECT pm.id proModelId, pm.p_name, pm.competition_number AS number, pm.year, pm.stage, pm.pro_type, pm.type, pm.pro_category, (SELECT label FROM sys_dict dict WHERE TYPE="0000000263" AND dict.value = pm.final_status) AS levelName, (SELECT label FROM sys_dict dict WHERE TYPE="project_type" AND dict.value = pm.type) AS proCategoryName, pm.final_result AS finalStatus,
(CASE WHEN (pm.pro_type = '1,') THEN pm.final_status WHEN (pm.pro_type = '7,') THEN pm.level ELSE '' END) AS "level", pm.short_name, pm.introduction, a.source AS source, GROUP_CONCAT(sdse.label SEPARATOR ',') AS sourceName, a.result_type AS resultType, GROUP_CONCAT(sdrt.label SEPARATOR ',') AS resultTypeName, a.result_content AS resultContent, a.budget AS budget, a.budget AS budgetDollar, a.innovation AS innovation, a.plan_step AS planStep, a.plan_content AS planContent, a.plan_start_date AS planStartDate, a.plan_end_date AS planEndDate, t.id teamId, o6.name "office_name", lstu.name "leader.name", lstu.no "leader.no", lop.name "leader.professional", lss.enterDate "leader.enterDate", lss.graduation "leader.graduation", sd.label "leader.education", lstu.id_no "leader.id_no", lstu.mobile "leader.mobile", lstu.email "leader.email", '' AS "leader.teacherTecTitle" FROM pro_model pm LEFT JOIN pro_model_tlxy a ON pm.id = a.model_id AND pm.del_flag = '0' LEFT JOIN act_yw yw ON yw.id = pm.actyw_id LEFT JOIN sys_user u ON u.id = pm.declare_id LEFT JOIN sys_office o6 ON o6.id = u.office_id LEFT JOIN team t ON t.id = pm.team_id /** 团队负责人 **/ LEFT JOIN sys_user lstu ON lstu.id = pm.declare_id LEFT JOIN sys_student_expansion lss ON lstu.id = lss.user_id LEFT JOIN sys_office lop ON lop.id = lstu.professional LEFT JOIN sys_dict sd ON lstu.education = sd.value AND sd.type = 'enducation_level' LEFT JOIN sys_dict sdse ON FIND_IN_SET(sdse.value, a.result_type) AND sdse.type = 'project_source' LEFT JOIN sys_dict sdrt ON FIND_IN_SET(sdrt.value, a.result_type) AND sdrt.type = 'project_result_type'
yw.id = #{actYwId} AND pm.del_flag = '0'
AND pm.year = #{proModel.year}
AND o6.id = #{proModel.deuser.office.id}
AND ( pm.p_name LIKE concat('%',#{proModel.queryStr},'%') OR pm.competition_number LIKE concat('%',#{proModel.queryStr},'%') OR u.name LIKE concat('%',#{proModel.queryStr},'%') )
AND pm.id in
#{id}
GROUP BY pm.id

SELECT

FROM pro_model_tlxy a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM pro_model_tlxy a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM pro_model_tlxy a


	a.del_flag ='0' and pm.proc_ins_id is not null

	AND  pm.actyw_id= #{actywId}


		AND pm.pro_category = #{proCategory}



		AND pm.p_name LIKE concat('%',#{pName},'%')


		AND (
			pm.p_name LIKE concat('%',#{queryStr},'%')
			OR pm.competition_number LIKE concat('%',#{queryStr},'%')
			OR u.name LIKE concat('%',#{queryStr},'%')
		)


ORDER BY  a.update_date DESC
limit #{offset},#{pageSize}

mapper.update:
UPDATE pro_model_tlxy SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE pro_model SET
	competition_number = #{pnum}
WHERE id = #{id}

UPDATE pro_model_tlxy SET
	x_competition_number = #{num}
WHERE model_id = #{proModelId}

UPDATE pro_model_tlxy SET
	g_competition_number = #{num}
WHERE model_id = #{proModelId}

UPDATE pro_model_tlxy SET
	p_competition_number = #{num}
WHERE model_id = #{proModelId}

mapper.select:
select count(1) from pro_model_tlxy pt
LEFT JOIN pro_model p on pt.model_id=p.id
where pt.p_competition_number=#{num}
or pt.g_competition_number=#{num}
or pt.x_competition_number=#{num}

select count(1) from pro_model_tlxy pt
LEFT JOIN pro_model p on pt.model_id=p.id
where pt.p_competition_number=#{num}
or pt.g_competition_number=#{num}
or pt.x_competition_number=#{num}

        select count(1) from pro_model t where t.del_flag='0' and pro_type='1,'
		and t.p_name=#{pname}

		and t.id !=#{pid}

		and t.type=#{type}

SELECT

FROM pro_model_tlxy a


	a.del_flag = 0

		AND pm.id in

			#{item}



		AND pm.competition_number = #{proModel.competitionNumber}


		AND pm.pro_category in

			#{item}



		AND pm.final_status in

			#{item}




		AND o6.id in

			#{item}



		AND pm.sub_time >= #{proModel.beginDate}


		AND pm.sub_time <=  #{proModel.endDate}



		AND pm.year = #{proModel.year}



		AND pm.p_name LIKE concat('%',#{proModel.pName},'%')



			AND u.name LIKE concat('%',#{proModel.deuser.name},'%')



		AND (
		pm.p_name LIKE concat('%',#{proModel.queryStr},'%')
		OR pm.competition_number LIKE concat('%',#{proModel.queryStr},'%')
		OR u.name LIKE concat('%',#{proModel.queryStr},'%')
		OR  LIKE concat('%',#{proModel.queryStr},'%')
		OR  LIKE concat('%',#{proModel.queryStr},'%')
		)





		ORDER BY ${page.orderBy} ${page.orderByType}



			ORDER BY find_in_set(pm.id,REPLACE(
			${id},' ','')
			)

SELECT

FROM pro_model_tlxy a


	a.del_flag = 0

		AND a.id in

			#{item}




			AND pm.id in

				#{item}




		AND pm.competition_number = #{proModel.competitionNumber}


		AND pm.pro_category in

			#{item}



		AND pm.final_status in

			#{item}




		AND o6.id in

			#{item}



		AND pm.sub_time >= #{proModel.beginDate}


		AND pm.sub_time <=  #{proModel.endDate}



		AND pm.year = #{proModel.year}



		AND pm.p_name LIKE concat('%',#{proModel.pName},'%')



			AND u.name LIKE concat('%',#{proModel.deuser.name},'%')



		AND (
		pm.p_name LIKE concat('%',#{proModel.queryStr},'%')
		OR pm.competition_number LIKE concat('%',#{proModel.queryStr},'%')
		OR u.name LIKE concat('%',#{proModel.queryStr},'%')
		)





		ORDER BY ${page.orderBy} ${page.orderByType}



			ORDER BY find_in_set(pm.id,REPLACE(
			${id},' ','')
			)

select * from (
SELECT

,ifnull(tempayg.cc,0) cc
FROM pro_model_tlxy a

left join (
select ayg.promodel_id,(case when count(ayg.promodel_id)=0 then 0 else 1 end ) cc from act_yw_gassign ayg
where ayg.del_flag='0' and ayg.gnode_id=#{proModel.gnodeId} and (

	ayg.promodel_id=#{item}

)
group by ayg.promodel_id
) tempayg on tempayg.promodel_id=pm.id
left join act_ru_execution ex
on ex.ACT_ID_ like concat('%',#{proModel.gnodeId},'%') and ex.PROC_INST_ID_=pm.proc_ins_id
and ex.IS_ACTIVE_='0' and ex.IS_CONCURRENT_='1'

	a.del_flag = 0 and ex.ID_ is null

		AND pm.id in

			#{item}




		AND pm.pro_category in

			#{item}



		AND pm.final_status in

			#{item}




		AND o6.id in

			#{item}



		AND pm.sub_time >= #{proModel.beginDate}


		AND pm.sub_time <=  #{proModel.endDate}


		AND pm.competition_number = #{proModel.competitionNumber}


		AND pm.year = #{proModel.year}


		AND pm.p_name LIKE concat('%',#{proModel.pName},'%')


		AND (
		pm.p_name LIKE concat('%',#{proModel.queryStr},'%')
		OR pm.competition_number LIKE concat('%',#{proModel.queryStr},'%')
		OR u.name LIKE concat('%',#{proModel.queryStr},'%')
		)



		and (tempayg.cc is null or tempayg.cc=0)


		and tempayg.cc=1


) temtb order by temtb.cc,temtb.`proModel.subTime`
