mapper.select:
SELECT 

FROM pw_appointment_rule a

WHERE a.id = #{id}

SELECT 

FROM pw_appointment_rule a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.is_auto = #{isAuto}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM pw_appointment_rule a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pw_appointment_rule SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
