mapper.select:
SELECT 

FROM pw_bill a

WHERE a.id = #{id}

SELECT 

FROM pw_bill a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.type = #{type}


		AND a.status = #{status}


		AND a.team_id = #{teamId}


		AND a.cfrom_date = #{cfromDate}


		AND a.cto_date = #{ctoDate}


		AND a.settled = #{settled}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM pw_bill a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pw_bill SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
