mapper.select:
SELECT 

FROM pw_bill_detail a

WHERE a.id = #{id}

SELECT 

FROM pw_bill_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.bid = #{bid}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM pw_bill_detail a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pw_bill_detail SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
