mapper.select:
SELECT 

FROM pw_bill_precords a

WHERE a.id = #{id}

SELECT 

FROM pw_bill_precords a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.bid = #{bid}


		AND a.pay_name LIKE 
			'%'||#{payName}||'%'
			'%'+#{payName}+'%'
			concat('%',#{payName},'%')


		AND a.pay_time >= #{payTime}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM pw_bill_precords a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pw_bill_precords SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
