mapper.select:
SELECT

FROM pw_enter_rel a

WHERE a.id = #{id}

SELECT

FROM pw_enter_rel a


	a.del_flag = #{DEL_FLAG_NORMAL}


			AND a.edid = #{pwEnterDetail.id}



				AND b.id = #{pwEnterDetail.pwEnter.id}



					AND b.applicant = #{pwEnterDetail.pwEnter.applicant.id}






			AND a.apply_id = #{actYwApply.id}


GROUP BY a.apply_id



		ORDER BY ${page.orderBy} ${page.orderByType}, b.applicant ASC, b.status ASC, b.no DESC, a.update_date DESC


		ORDER BY b.applicant ASC, b.status ASC, b.no DESC, a.update_date DESC

SELECT

FROM pw_enter_rel a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pw_enter_rel SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
