mapper.select:
SELECT

FROM pw_fassets a

WHERE a.id = #{id}

SELECT

FROM pw_fassets a


	a.del_flag = #{DEL_FLAG_NORMAL}


			AND a.rid = #{pwRoom.id}


			AND r.name LIKE
			'%'||#{pwRoom.name}||'%'
			'%'+#{pwRoom.name}+'%'
			concat('%',#{pwRoom.name},'%')





			AND a.cid = #{pwCategory.id}




			AND cc.id = #{pwCategory.parent.id}



		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.brand = #{brand}


		AND a.specification = #{specification}


		AND a.prname = #{prname}


		AND a.phone = #{phone}


		AND a.mobile = #{mobile}


		AND a.time = #{time}


		AND a.status = #{status}


		AND a.resp_name LIKE
			'%'||#{respName}||'%'
			'%'+#{respName}+'%'
			concat('%',#{respName},'%')


		AND a.resp_phone = #{respPhone}


		AND a.resp_mobile = #{respMobile}


		AND a.start_date = #{startDate}




		ORDER BY ${page.orderBy} ${page.orderByType}


		ORDER BY a.update_date DESC

SELECT

FROM pw_fassets a


	a.del_flag = #{DEL_FLAG_NORMAL} AND a.rid = #{pwRoom.id}


			AND a.cid = #{pwCategory.id}



		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.brand = #{brand}


		AND a.specification = #{specification}


		AND a.prname = #{prname}


		AND a.phone = #{phone}


		AND a.mobile = #{mobile}


		AND a.time = #{time}


		AND a.status = #{status}


		AND a.resp_name LIKE
			'%'||#{respName}||'%'
			'%'+#{respName}+'%'
			concat('%',#{respName},'%')


		AND a.resp_phone = #{respPhone}


		AND a.resp_mobile = #{respMobile}


		AND a.start_date = #{startDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM pw_fassets a


	a.del_flag = #{DEL_FLAG_NORMAL} AND a.status = 0 AND (a.rid is NULL OR a.rid = '')


			AND a.cid = #{pwCategory.id}



		AND a.name LIKE
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.brand = #{brand}


		AND a.specification = #{specification}


		AND a.prname = #{prname}


		AND a.phone = #{phone}


		AND a.mobile = #{mobile}


		AND a.time = #{time}


		AND a.status = #{status}


		AND a.resp_name LIKE
			'%'||#{respName}||'%'
			'%'+#{respName}+'%'
			concat('%',#{respName},'%')


		AND a.resp_phone = #{respPhone}


		AND a.resp_mobile = #{respMobile}


		AND a.start_date = #{startDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM pw_fassets a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE pw_fassets



			when id=#{item.id} then #{item.pwRoom.id}




			when id=#{item.id} then #{item.status}




			when id=#{item.id} then #{item.respName}




			when id=#{item.id} then #{item.respPhone}




			when id=#{item.id} then #{item.respMobile}




			when id=#{item.id} then #{item.startDate}



WHERE id in

	#{item.id}

UPDATE pw_fassets SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE pw_fassets SET rid=NULL, resp_name=NULL, resp_phone=NULL, resp_mobile=NULL, start_date=NULL, status='0'
WHERE rid IN

	#{item}
