mapper.select:
SELECT

FROM pw_room a

WHERE a.id = #{id}

SELECT r.id AS "id", r.name AS "name", r.color AS "color", r.person AS "person", r.phone AS "phone", r.mobile AS "mobile", r.alias AS "alias", r.type AS "type", r.num AS "num", r.num_type AS "numtype", r.remainder_num AS "remaindernum", r.area AS "area", r.is_usable AS "isUsable", r.is_allowm AS "isAllowm", r.is_assign AS "isAssign", r.sid AS "pwSpace.id", r.update_date AS "updateDate", r.update_by AS "updateBy.id", r.create_date AS "createDate", r.create_by AS "createBy.id", r.remarks AS "remarks", r.del_flag AS "delFlag", IFNULL(s.roomordercount,0) as "roomordercount", IFNULL(h.hroomordercount,0) as "hroomordercount", a.status AS "pwAppointment.status", (SELECT count(r1.id) FROM pw_room r1 LEFT JOIN pw_appointment pa on r1.id = pa.rid AND r1.del_flag = '0' WHERE r1.id = r.id AND pa.`status` = 1 AND now()>= pa.start_date AND pa.end_date >= now() ) as orderusing, (SELECT count(r1.id) FROM pw_room r1 LEFT JOIN pw_appointment pa on r1.id = pa.rid AND r1.del_flag = '0' WHERE r1.id = r.id AND pa.`status` = 1 AND pa.start_date >= now()
) as ordernotuse, b.parent_id AS "pwSpace.parent.id", b.parent_ids AS "pwSpace.parentIds", b.name AS "pwSpace.name", b.person AS "pwSpace.person", b.type AS "pwSpace.type", b.phone AS "pwSpace.phone", b.mobile AS "pwSpace.mobile", b.open_week AS "pwSpace.openWeek", b.am_open_start_time AS "pwSpace.amOpenStartTime", b.am_open_end_time AS "pwSpace.amOpenEndTime", b.pm_open_start_time AS "pwSpace.pmOpenStartTime", b.pm_open_end_time AS "pwSpace.pmOpenEndTime", b.floor_num AS "pwSpace.floorNum", b.area AS "pwSpace.area", b.update_date AS "pwSpace.updateDate", b.update_by AS "pwSpace.updateBy.id", b.create_date AS "pwSpace.createDate", b.create_by AS "pwSpace.createBy.id", b.remarks AS "pwSpace.remarks", b.del_flag AS "pwSpace.delFlag", p.parent_id AS "pwSpace.parent.parent.id", p.parent_ids AS "pwSpace.parent.parentIds", p.name AS "pwSpace.parent.name", pp.parent_id AS "pwSpace.parent.parent.parent.id", pp.parent_ids AS "pwSpace.parent.parent.parentIds", pp.name AS "pwSpace.parent.parent.name"
from pw_room r LEFT JOIN pw_appointment a on a.rid = r.id and a.del_flag=0 LEFT JOIN pw_space b ON b.id = r.sid LEFT JOIN pw_space p ON p.id = b.parent_id LEFT JOIN pw_space pp ON pp.id = p.parent_id LEFT JOIN (SELECT a.id,a.rid,count(a.id) as roomordercount from pw_appointment a where a.del_flag=0 and a.`status`=1 group by a.rid) s on s.id=a.id LEFT JOIN (SELECT a.id,a.rid,count(a.id) as hroomordercount from pw_appointment a where a.del_flag=0 and a.`status` in (1,3) group by a.rid) h on h.id=a.id where r.is_usable=1 and r.del_flag = #{DEL_FLAG_NORMAL}
and (r.name like '%${keys}%' or b.name like '%${keys}%' or p.name like '%${keys}%' or pp.name like '%${keys}%' )
AND r.name LIKE concat('%',#{name},'%')
AND r.type = #{type}
AND r.num >= #{querynumbegin} and r.num_type = 2
AND #{querynumend} >= r.num and r.num_type = 2
AND r.num >= #{querypeoplebegin} and r.num_type = 1
AND #{querypeopleend} >= r.num and r.num_type = 1
AND r.area >= #{queryareabegin}
AND #{queryareaend} >= r.area
AND (SELECT count(r1.id) FROM pw_room r1 LEFT JOIN pw_appointment pa on r1.id = pa.rid AND r1.del_flag = '0' WHERE r1.id = r.id AND pa.`status` = 1 AND now()>= pa.start_date AND pa.end_date >= now() ) > 0
AND (SELECT count(r1.id) FROM pw_room r1 LEFT JOIN pw_appointment pa on r1.id = pa.rid AND r1.del_flag = '0' WHERE r1.id = r.id AND pa.`status` = 1 AND pa.start_date >= now()
) > 0
AND (SELECT count(r1.id) FROM pw_room r1 LEFT JOIN pw_appointment pa on r1.id = pa.rid AND r1.del_flag = '0' WHERE r1.id = r.id AND pa.`status` = 1 AND pa.start_date >= now()
) =0 AND (SELECT count(r1.id) FROM pw_room r1 LEFT JOIN pw_appointment pa on r1.id = pa.rid AND r1.del_flag = '0' WHERE r1.id = r.id AND pa.`status` = 1 AND now()>= pa.start_date AND pa.end_date >= now() ) =0
AND r.sid in
#{sid}
GROUP BY r.id
ORDER BY ${page.orderBy} ${page.orderByType}, pp.parent_id DESC, p.parent_id DESC, b.parent_id DESC
ORDER BY r.create_date DESC

SELECT

FROM pw_room a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.name LIKE

        concat('%',#{name},'%')


        AND a.alias LIKE

        concat('%',#{alias},'%')


        AND a.person = #{person}


        AND a.phone = #{phone}


        AND a.mobile = #{mobile}


        AND a.type = #{type}


        AND a.num = #{num}


        AND a.remainder_num  >=  #{remaindernum}


        AND a.is_usable = #{isUsable}


        AND a.is_allowm = #{isAllowm}


        AND a.num_type = #{numtype}


        AND a.is_assign = #{isAssign}



            AND a.sid = #{pwSpace.id}





        ORDER BY ${page.orderBy} ${page.orderByType}, pp.parent_id DESC, p.parent_id DESC, b.parent_id DESC


        ORDER BY pp.parent_id DESC, p.parent_id DESC, b.parent_id DESC, a.update_date DESC

SELECT ( SELECT count(r1.id) FROM pw_room r1 LEFT JOIN pw_appointment pa ON r1.id = pa.rid AND r1.del_flag = '0' WHERE r1.id = a.id AND pa.`status` = 1 AND now()>= pa.start_date AND pa.end_date >= now() ) AS 'isYY', (SELECT count(r2.id) from pw_room r2 LEFT JOIN pw_enter_room per on per.rid = r2.id and per.del_flag=0 LEFT JOIN pw_enter e on e.id = per.eid and e.del_flag=0 where r2.id=a.id and e.`status` in (10,30,40) and e.restatus in(11,20) and now()>= e.start_date and e.end_date >= now() ) AS 'isFP',
FROM pw_room a
a.del_flag = #{DEL_FLAG_NORMAL}
and (a.name like '%${keys}%' or b.name like '%${keys}%' or p.name like '%${keys}%' or pp.name like '%${keys}%' or ppp.name like '%${keys}%' or pppp.name like '%${keys}%')
AND a.name LIKE
concat('%',#{name},'%')
and ( SELECT count(r1.id) FROM pw_room r1 LEFT JOIN pw_appointment pa ON r1.id = pa.rid AND r1.del_flag = '0' WHERE r1.id = a.id AND pa.`status` = 1 AND now() >= pa.start_date AND pa.end_date >= now() ) >0
and (SELECT count(r2.id) from pw_room r2 LEFT JOIN pw_enter_room per on per.rid = r2.id and per.del_flag=0 LEFT JOIN pw_enter e on e.id = per.eid and e.del_flag=0 where r2.id=a.id and e.`status` in (10,30,40) and e.restatus in(11,20) and now() >= e.start_date and e.end_date >= now() ) >0
and ( SELECT count(r1.id) FROM pw_room r1 LEFT JOIN pw_appointment pa ON r1.id = pa.rid AND r1.del_flag = '0' WHERE r1.id = a.id AND pa.`status` = 1 AND now() >= pa.start_date AND pa.end_date >= now() ) =0 and (SELECT count(r2.id) from pw_room r2 LEFT JOIN pw_enter_room per on per.rid = r2.id and per.del_flag=0 LEFT JOIN pw_enter e on e.id = per.eid and e.del_flag=0 where r2.id=a.id and e.`status` in (10,30,40) and e.restatus in(11,20) and now() >= e.start_date and e.end_date >= now() ) =0
AND a.alias LIKE
concat('%',#{alias},'%')
AND a.person = #{person}
AND a.phone = #{phone}
AND a.mobile = #{mobile}
AND a.type = #{type}
AND a.num = #{num}
AND a.num_type = '1' AND a.num >= #{querypeoplebegin}
AND a.num_type = '1' AND a.num <= #{querypeopleend}
AND a.num_type = '2' AND a.num >= #{querynumbegin}
AND a.num_type = '2' AND a.num <= #{querynumend}
AND a.area >= #{queryareabegin}
AND a.area <= #{queryareaend}
AND a.is_usable = #{isUsable}
AND a.is_allowm = #{isAllowm}
AND a.is_assign = #{isAssign}
AND a.sid in
#{sid}
ORDER BY ${page.orderBy} ${page.orderByType}, pp.parent_id DESC, p.parent_id DESC, b.parent_id DESC
ORDER BY a.create_date DESC

SELECT
(SELECT count(a1.id) from pw_room a1 where  a1.remainder_num=0 and a1.del_flag=0 and a1.id = a.id) as fullroom,
(SELECT count(a2.id) from pw_room a2 where  a2.remainder_num !=0 and a2.remainder_num != a2.num and a2.del_flag=0 and a2.id = a.id) as notfullroom,

FROM pw_room a


    a.del_flag = #{DEL_FLAG_NORMAL}

        and (a.name like '%${keys}%' or  b.name like '%${keys}%' or  p.name like '%${keys}%'
        or  pp.name like '%${keys}%' or  ppp.name like '%${keys}%' or  pppp.name like '%${keys}%')


        AND a.name LIKE

        concat('%',#{name},'%')


        AND a.alias LIKE

        concat('%',#{alias},'%')


        AND a.person = #{person}


        AND a.phone = #{phone}


        AND a.mobile = #{mobile}


        AND a.type = #{type}


        AND a.num = #{num}


    	AND a.num_type = '1'
        AND a.num   >=  #{querypeoplebegin}


    	AND a.num_type = '1'
        AND a.num  <=  #{querypeopleend}


    	AND a.num_type = '2'
        AND a.num   >=  #{querynumbegin}


    	AND a.num_type = '2'
        AND a.num  <=  #{querynumend}


        AND a.area   >=  #{queryareabegin}


        AND a.area  <=  #{queryareaend}


        AND a.is_usable = #{isUsable}


        AND a.is_allowm = #{isAllowm}


        AND a.is_assign = #{isAssign}


        AND (SELECT count(a1.id) from pw_room a1 where  a1.remainder_num=0 and a1.del_flag=0 and a1.id = a.id) >0


        AND (SELECT count(a2.id) from pw_room a2 where  a2.remainder_num !=0 and a2.remainder_num != a2.num and a2.del_flag=0 and a2.id = a.id) >0


        AND (SELECT count(a1.id) from pw_room a1 where  a1.remainder_num=0 and a1.del_flag=0 and a1.id = a.id) = 0
        AND (SELECT count(a2.id) from pw_room a2 where  a2.remainder_num !=0 and a2.remainder_num != a2.num and a2.del_flag=0 and a2.id = a.id) = 0


        AND a.sid in

            #{sid}





        ORDER BY ${page.orderBy} ${page.orderByType}, pp.parent_id DESC, p.parent_id DESC, b.parent_id DESC


        ORDER BY a.create_date DESC

SELECT

FROM pw_room a



    a.del_flag = #{DEL_FLAG_NORMAL} AND a.is_assign = 1

        AND a.name LIKE

        concat('%',#{name},'%')


        AND a.alias LIKE

       concat('%',#{alias},'%')


        AND a.person = #{person}


        AND a.phone = #{phone}


        AND a.mobile = #{mobile}


        AND a.num = #{num}


        AND a.is_usable = #{isUsable}


        AND a.is_assign = #{isAssign}


        AND a.sid in

            #{sid}





        ORDER BY ${page.orderBy} ${page.orderByType}, pp.parent_id DESC, p.parent_id DESC, b.parent_id DESC


        ORDER BY pp.parent_id DESC, p.parent_id DESC, b.parent_id DESC, a.update_date DESC

SELECT

FROM pw_room a


    a.del_flag = 0 AND a.sid = #{sid} AND a.name = #{name}

