mapper.select:
SELECT 

FROM sco_affirm_criterion_couse a

WHERE a.id = #{id}

SELECT 

FROM sco_affirm_criterion_couse a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sco_affirm_criterion_couse a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sco_affirm_criterion_couse SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
