mapper.select:
SELECT 

FROM sco_course a

WHERE a.id = #{id}

SELECT
FROM sco_course a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT a.id AS "scoCourse.id", a.code AS "scoCourse.code", a.name AS "scoCourse.name", a.type AS "scoCourse.type", a.nature AS "scoCourse.nature", a.professional AS "scoCourse.professional", a.plan_score AS "scoCourse.planScore", a.plan_time AS "scoCourse.planTime", a.over_score AS "scoCourse.overScore", a.create_by AS "scoCourse.createBy.id", a.create_date AS "scoCourse.createDate", a.update_by AS "scoCourse.updateBy.id", a.update_date AS "scoCourse.updateDate", a.remarks AS "scoCourse.remarks", a.del_flag AS "scoCourse.delFlag", su.no AS "user.no", su.name AS "user.name", su.photo AS "user.photo", su.professional AS "user.professional", so.id AS "office.id", so.name AS "office.name", sa.id AS "scoApply.id", sa.proc_ins_id AS "scoApply.procInsId", sa.user_id AS "scoApply.userId", sa.real_time AS "scoApply.realTime", sa.real_score AS "scoApply.realScore", sa.score AS "scoApply.score", sa.course_status AS "scoApply.courseStatus", sa.audit_status AS "scoApply.auditStatus", IF(sa.audit_status = '2', '0',sa.audit_status) AS "orderState", sa.del_flag AS "scoApply.delFlag" FROM sco_course a left JOIN sco_apply sa on sa.course_id = a.id inner join sys_user su on su.id=sa.user_id inner join sys_office so on so.id=su.office_id
a.del_flag = #{DEL_FLAG_NORMAL} and sa.del_flag = #{DEL_FLAG_NORMAL} and sa.audit_status != 0
AND (a.name LIKE concat('%',#{keyWord},'%') or a.code LIKE concat('%',#{keyWord},'%') or su.no LIKE concat('%',#{keyWord},'%') )
AND su.no LIKE '%'||#{user.no}||'%' '%'+#{user.no}+'%' concat('%',#{user.no},'%')
AND so.id =#{ office.id}
AND su.professional =#{user.professional }
AND a.code =#{scoCourse.code}
AND a.type=#{scoCourse.type}
AND sa.audit_status =#{scoApply.auditStatus}
ORDER BY ${page.orderBy}
ORDER BY orderState

SELECT 

FROM sco_course a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

select count(1) from
sco_course a
where a.del_flag='0' and a.name=#{name}

	and a.id!=#{id}

select count(1) from
sco_course a
where a.del_flag='0' and a.code=#{code}

	and a.id!=#{id}
