mapper.select:
SELECT

FROM sco_score a

WHERE a.id = #{id}

SELECT
FROM sco_score a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.user_id = #{user.id}
AND u2.name LIKE '%'||#{user.name}||'%' '%'+#{user.name}+'%' concat('%',#{user.name},'%')
AND u2.no LIKE '%'||#{user.no}||'%' '%'+#{user.no}+'%' concat('%',#{user.no},'%')
AND c.company.id = #{user.company.id}
AND c.name LIKE '%'||#{user.company.name}||'%' '%'+#{user.company.name}+'%' concat('%',#{user.company.name},'%')
AND o.id = #{user.office.id}
AND o.name LIKE '%'||#{user.office.name}||'%' '%'+#{user.office.name}+'%' concat('%',#{user.office.name},'%')
ORDER BY a.user_id DESC, ${page.orderBy}
ORDER BY a.user_id DESC, a.update_date DESC

SELECT
FROM sco_score a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.user_id = #{user.id}
AND u2.name LIKE '%'||#{user.name}||'%' '%'+#{user.name}+'%' concat('%',#{user.name},'%')
AND u2.no LIKE '%'||#{user.no}||'%' '%'+#{user.no}+'%' concat('%',#{user.no},'%')
AND c.company.id = #{user.company.id}
AND c.name LIKE '%'||#{user.company.name}||'%' '%'+#{user.company.name}+'%' concat('%',#{user.company.name},'%')
AND o.id = #{user.office.id}
AND o.name LIKE '%'||#{user.office.name}||'%' '%'+#{user.office.name}+'%' concat('%',#{user.office.name},'%')
GROUP BY a.user_id
ORDER BY ${page.orderBy} ${page.orderByType}
ORDER BY a.update_date DESC

SELECT

FROM sco_score a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sco_score SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
